package com.epam.laba;import com.epam.laba.hometask1.config.BeansConfig;import com.epam.laba.hometask1.pet.Pet;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.AnnotationConfigApplicationContext;@SpringBootApplicationpublic class LabaApplication {    public static void main(String[] args) {        ApplicationContext applicationContext = new AnnotationConfigApplicationContext(BeansConfig.class);        Pet pet = applicationContext.getBean(Pet.class);        pet.printPets();    }}